package com;

import java.util.Scanner;

public class Tabla_Mutiplicar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  
	        int n=12;
	        int res=0;
	        
	        System.out.println("Tabla del " + n);
	        for(int i = 1; i<=10; i++){
	        	
	        	res=n*i;
	             System.out.println(n + " * " + i + " = " + res);                                                     
	        }
		
	}

}
